import React from 'react';
import './App.css';

const recette = {
  nom: '',
  ingredients: [],
  instructions: ''
}

let recettesList = [];

class Recettes extends React.Component {
  constructor(props){
    super(props);
    this.state = {

    };
  }

  render(){

    return (
      <div>RECETTES</div>
      //liste des recettes
    )
  }
};

class Ajouter extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      ingredientsCounter: 6,
      recettes: 0,
      nom: '',
      ingredients: [],
      instructions: ''
    };
    this.newIngredient = this.newIngredient.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleName = this.handleName.bind(this);
    this.handleIngredients = this.handleIngredients.bind(this);
    this.handleInstructions = this.handleInstructions.bind(this);
  }

  newIngredient(e){
    e.preventDefault();
    return this.setState({ingredients: this.state.ingredients + 1})
  };

  handleName(e){
    return this.setState({name: e.target.value})
  };

  handleIngredients(e){
    return this.setState({ingredients: this.state.ingredients.push(e.target.value)})
  };

  handleInstructions(e){
    return this.setState({instructions: e.target.value})
  }

  handleSubmit(){
    let nouvelle = new recette();
    recette.nom = this.state.name;
    recette.ingredients = this.state.ingredients;
    recette.instructions = this.state.instructions;

    recettesList.push(nouvelle);
  };

  render(){

    return (
      <div>
        <h2>Ajouter une nouvelle recette :</h2>

        <form>
          <label htmlFor="name">Nom</label><input type="text" id="name" onChange={this.handleName}></input>
          <br />
          <fieldset>
            <legend>Ingr√©dients</legend>
            <div id="inputs">
              {Array.from(Array(this.state.ingredientsCounter)).map(item => {
                return <input type="text" class="ingredient" onChange={this.handleIngredients}></input>
              })}
            </div>
            <button onClick={this.newIngredient}>+</button>
          </fieldset>
          <label htmlFor="instructions">Instructions</label><textarea id="instructions" onChange={this.handleInstructions}></textarea>

          <button type="submit" onClick={this.handleSubmit}>Envoyer</button>
        </form>
      </div>
    )
  }
};

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      content: <Recettes />
    };
    this.add = this.add.bind(this);
    this.recette = this.recette.bind(this);
  }

    add(){
      return this.setState({content: <Ajouter />})
    }

    recette(){
      return this.setState({content: <Recettes />})
    }
  render(){

    return (
      <div>
        <h1>Mes recettes</h1>
        <button onClick={this.add}>Ajouter</button>
        <button onClick={this.recette}>Recettes</button>
        <div id="main">{this.state.content}</div>
      </div>
    )
  }
}

export default App;


/*User Story: I can create recipes that have names and ingredients.

User Story: I can see an index view where the names of all the recipes are visible.

User Story: I can click into any of those recipes to view it.

User Story: I can edit these recipes.

User Story: I can delete these recipes.

User Story: All new recipes I add are saved in my browser's local storage. If I refresh the page, these recipes will still be there.*/
