import React from 'react';
import './App.css';

function recette (n, i1, i2) {
    this.nom = n;
    this.ingredients = i1;
    this.instructions = i2;
}

let recettesList = [];

class Recettes extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      nom: '',
      ingredients: {},
      instructions: ''
    };
    this.getRecipe = this.getRecipe.bind(this);
    this.edit = this.edit.bind(this);
    this.saveModifs = this.saveModifs.bind(this);
    this.delete = this.delete.bind(this);
    this.close = this.close.bind(this);
  }

  getRecipe(e){
    let selectedRecipe = recettesList.filter(toFind => toFind.nom === e.target.id)[0];
    this.setState({nom: selectedRecipe.nom, ingredients: selectedRecipe.ingredients, instructions: selectedRecipe.instructions});

    document.getElementById("boutons-recette").style.display = "block";
    document.getElementById("recette-detail").style.display = "block";
  };

  edit(){
    document.getElementById("nom-recette").contentEditable = "true";
    document.getElementById("ingredients-recette").contentEditable = "true";
    document.getElementById("instructions-recette").contentEditable = "true";

    document.getElementById("edit-fini").style.display = "block";
  };

  saveModifs(){
    if(document.getElementById("nom-recette").innerHTML !== '' && document.getElementById("ingredients-recette").innerHTML !== '' && document.getElementById("instructions-recette").innerHTML !== ''){
      document.getElementById("nom-recette").contentEditable = "false";
      document.getElementById("ingredients-recette").contentEditable = "false";
      document.getElementById("instructions-recette").contentEditable = "false";

      document.getElementById("edit-fini").style.display = "none";
      document.getElementById("error-edit").style.display = "none";

      let selectedRecipe = recettesList.filter(toFind => toFind.nom === this.state.nom)[0];
      let indexToReplace = recettesList.indexOf(selectedRecipe);

      let newIngredientsArr = document.getElementById("ingredients-recette").innerHTML.split(/\n/);
      let newIngredients = {};
      for(let i = 0; i < newIngredientsArr.length; i++){
        newIngredients[i] = newIngredientsArr[i];
      };

      let updatedRecipe = new recette(document.getElementById("nom-recette").innerHTML, newIngredients, document.getElementById("instructions-recette").innerHTML);

      recettesList.splice(indexToReplace, 1, updatedRecipe);

      this.setState({
        nom: document.getElementById("nom-recette").innerHTML,
        ingredients: newIngredients,
        instructions: document.getElementById("instructions-recette").innerHTML
      })

    } else {
      document.getElementById("error-edit").style.display = "block";
    }
  }

  close(){
    document.getElementById("recette-detail").style.display = "none";
  };

  delete(){
    let selectedRecipe = recettesList.filter(toFind => toFind.nom === this.state.nom)[0];
    let indexToDelete = recettesList.indexOf(selectedRecipe);
    recettesList.splice(indexToDelete, 1);

    this.setState({
      nom: '',
      ingredients: {},
      instructions: ''
    })

    this.close();
  };

  render(){

    if(recettesList.length === 0){
      return (
        <div>
          <h3>Aucune recette enregistrée</h3>
        </div>
      )
    } else {
    return (
      <div>
        <h2>Toutes les recettes</h2>
        <div id="liste-recettes">
          {recettesList.map(item => (<p onClick={this.getRecipe} id={item.nom}>{item.nom}</p>))}
        </div>
        <div id="recette-detail">
          <div id="boutons-recette">
            <button title="Editer" onClick={this.edit}>✎</button>
            <button title="Supprimer" onClick={this.delete}>🗑</button>
            <button title="Fermer" onClick={this.close}>X</button>
          </div>
          <h3 id="nom-recette">{this.state.nom}</h3>
          <ul id="ingredients-recette">
            {Object.keys(this.state.ingredients).map(key => {
              if(this.state.ingredients[key]){
                return <li>{this.state.ingredients[key]}</li>
              } else {
                return null;
              }
            })}
          </ul>
          <p id="instructions-recette">{this.state.instructions}</p>
          <button id="edit-fini" onClick={this.saveModifs}>J'ai terminé</button>
          <p id="error-edit">Champ(s) manquant(s)</p>
        </div>
      </div>
    )
  }
  }
};

class Ajouter extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      ingredientsCounter: [0, 1, 2, 3, 4, 5],
      nom: '',
      ingredients: {0 : '', 1: '', 2: '', 3: '', 4: '', 5:''},
      instructions: ''
    };
    this.newIngredient = this.newIngredient.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleName = this.handleName.bind(this);
    this.handleIngredients = this.handleIngredients.bind(this);
    this.handleInstructions = this.handleInstructions.bind(this);
  }

  newIngredient(e){
    e.preventDefault();
    return this.setState({ingredientsCounter: [...this.state.ingredientsCounter, this.state.ingredientsCounter[this.state.ingredientsCounter.length - 1] + 1],
                          ingredients: {...this.state.ingredients, [this.state.ingredientsCounter[this.state.ingredientsCounter.length - 1] + 1]: ''}})
  };

  handleName(e){
    document.getElementById("success").style.display = "none";
    return this.setState({name: e.target.value})
  };

  handleIngredients(e){
    document.getElementById("success").style.display = "none";
    let newIngredients = this.state.ingredients
    newIngredients[e.target.name] = e.target.value
    return this.setState({ingredients: newIngredients})
  };

  handleInstructions(e){
    document.getElementById("success").style.display = "none";
    return this.setState({instructions: e.target.value})
  }

  handleSubmit(e){
    if(this.state.name && this.state.ingredients[0] && this.state.instructions){
      e.preventDefault();

      let item = new recette(this.state.name, this.state.ingredients, this.state.instructions);

      recettesList.push(item);

      document.getElementById("recipe-form").reset();
      document.getElementById("success").style.display = "block";

      this.setState({ingredientsCounter: [0, 1, 2, 3, 4, 5],
        nom: '',
        ingredients: {0 : '', 1: '', 2: '', 3: '', 4: '', 5:''},
        instructions: ''})
    }
  };

  render(){

    return (
      <div>
        <h2>Ajouter une nouvelle recette :</h2>

        <form id="recipe-form">
          <label htmlFor="name">Nom</label><input type="text" id="name" defaultValue={this.state.nom} onChange={this.handleName} required></input>
          <br />
          <fieldset>
            <legend>Ingrédients</legend>
            <div id="inputs">
              {Array.from(this.state.ingredientsCounter).map(item => {
                if(item === 0){
                  return <input type="text" className="ingredient" onChange={this.handleIngredients} name={item} defaultValue={this.state.ingredients[item]} required></input>
                } else {
                  return <input type="text" className="ingredient" onChange={this.handleIngredients} name={item} defaultValue={this.state.ingredients[item]}></input>
                }
              })}
            </div>
            <button onClick={this.newIngredient}>+</button>
          </fieldset>
          <label htmlFor="instructions">Instructions</label><textarea id="instructions" defaultValue={this.state.instructions} onChange={this.handleInstructions} required></textarea>
          <br />
          <button type="submit" onClick={this.handleSubmit}>Envoyer</button>
        </form>
        <p id="success">Recette ajoutée !</p>
      </div>
    )
  }
};

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      content: <Recettes />
    };
    this.add = this.add.bind(this);
    this.recette = this.recette.bind(this);
  }

    add(){
      return this.setState({content: <Ajouter />})
    }

    recette(){
      return this.setState({content: <Recettes />})
    }
  render(){

    return (
      <div>
        <h1>Mes recettes</h1>
        <button onClick={this.recette}>Recettes</button>
        <button onClick={this.add}>Ajouter</button>
        <div id="main">{this.state.content}</div>
      </div>
    )
  }
}

export default App;


